$(document).ready(function(){$(".adminLink").live("click",function(b){b.preventDefault();var c=$(b.target).closest("li");$.ajax("/admin/julien.zmiro"+$(b.target).attr("href"),{type:"POST",dataType:"json",success:function(d){if(d){a(d,c)}},error:function(){console.log("ajax error")}})});function a(b,d){var c=d,e="";if(b.type=="add"){d.remove();$(".display ul").prepend(c);c.find(".adminLink").text("Remove this tweet");e=c.find(".adminLink").attr("href").substr(5);c.find(".adminLink").attr("href","/rm/"+e)}else{if(b.type=="rm"){d.remove();$(".pending ul").prepend(c);c.find(".adminLink").text("Add this tweet");e=c.find(".adminLink").attr("href").substr(4);c.find(".adminLink").attr("href","/add/"+e)}else{if(b.type=="ban"){d.remove()}}}}});